import React, { Component } from "react";
import axios from "axios";

class App extends Component {
  state = {
    data: {}
  }
  componentDidMount() {
    axios.get('https://api.openweathermap.org/data/2.5/onecall?lat=50.4291723&lon=2.8319805&units=metric&lang=fr&exclude=minutely,hourly,alerts&appid=8c3a54c385c9c9d874d88f2cd6b3dda8')
    .then(res => {
      this.setState({
        data: res.data
      })
    })
  }

  render() {
    const data = this.state.data;


    // Checker que data est vide
    if(Object.keys(data).length !== 0) {
      const icon_today = data.current.weather[0].icon;
      const img_today = `http://openweathermap.org/img/wn/${icon_today}@2x.png`

      // S'il est pas vide, alors this.state.data.current... a une valeur
      return (
        <div className="App">
          <h1>Ville de Lens</h1>
          <p>Météo actuelle:  {this.state.data.current.temp}° / Ciel dégagé</p>
          <p>Météo de demain : 4° / Pluvieux 
            <img src="http://openweathermap.org/img/wn/10d@2x.png"/>
          </p> 
          <p>Météo dans 2 jours : 7.12° / Orageux</p>
          <p>Météo dans 3 jours : 14° / Ensoleillé</p>
          <p>Météo dans 4 jours : 9° / Ensoleillé</p>
        </div>
      );
    }
    else {
      // Sinon j'affiche un loading
      return (<p>Loading...</p>);
    }


  }
}

export default App;
