import React, { Component } from "react";
import axios from "axios";

class App extends Component {
  componentDidMount() {
    axios.get('https://api.openweathermap.org/data/2.5/onecall?lat=50.4291723&lon=2.8319805&units=metric&lang=fr&exclude=minutely,hourly,alerts&appid=8c3a54c385c9c9d874d88f2cd6b3dda8')
    .then(res => {
      console.log(res.data);

      const data = res.data;

      // Toute la météo actuelle
      // console.log(data.current);

      // La température actuelle 
      const today = data.current;
      console.log(today.temp);
      // data.current.weather -> un tableau avec un seul objet contenant les infos du jour
      // data.current.weather[0] -> le seul objet du tableau
      console.log(today.weather[0].description);

      // La météo des prochains jours
      console.log(data.daily);

      // Toute la météo de demain
      const tomorrow = data.daily[0];
      console.log(tomorrow.temp.day);

      // La description du jour
      console.log(tomorrow.weather[0].description);

    })
  }

  render() {
    return (
      <div className="App">
        <h1>Ville de Lens</h1>
        <p>Météo actuelle: 2° / Ciel dégagé</p>
        <p>Météo de demain : 4° / Pluvieux</p> 
        <p>Météo dans 2 jours : 7.12° / Orageux</p>
        <p>Météo dans 3 jours : 14° / Ensoleillé</p>
        <p>Météo dans 4 jours : 9° / Ensoleillé</p>
      </div>
    );
  }
}

export default App;
